class Solution {
public:
  long long countSubarrays(vector<int>& nums, int k) {
    int64_t res = 0;
    unordered_map<int, int> prv_and, cur_and;
    for (auto num : nums) {
      if (num & k != k) {
        prv_and.clear();
        cur_and.clear();
        continue;
      }
      cur_and[num]++;
      for (auto [key, val] : prv_and) {
        if ((key & num & k) == k) {
          cur_and[key & num] += val;
        }
      }
      res += cur_and[k];
      swap(prv_and, cur_and);
      cur_and.clear();
    }
    return res;
  }
};
