import java.util.HashMap;
import java.util.Map;

class Solution {

    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        Map<Long, Long> res = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            ans += processElement(res, nums[i], k);
            res = new HashMap<>(processElementMap(res, nums[i]));
        }

        return ans;
    }

    private long processElement(Map<Long, Long> res, int num, int k) {
        Map<Long, Long> curr = new HashMap<>();
        long count = 0;

        if (num == k) {
            ++count;
        }
        curr.put((long) num, 1L);

        for (Map.Entry<Long, Long> entry : res.entrySet()) {
            long val = entry.getKey();
            long freq = entry.getValue();

            long nar = val & num;
            if (nar == k) {
                count += freq;
            }
            curr.put(nar, curr.getOrDefault(nar, 0L) + freq);
        }

        return count;
    }

    private Map<Long, Long> processElementMap(Map<Long, Long> res, int num) {
        Map<Long, Long> curr = new HashMap<>();
        curr.put((long) num, 1L);

        for (Map.Entry<Long, Long> entry : res.entrySet()) {
            long val = entry.getKey();
            long freq = entry.getValue();

            long nar = val & num;
            curr.put(nar, curr.getOrDefault(nar, 0L) + freq);
        }

        return curr;
    }
}
