class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<long long,long long> mpprev,mp;
        long long ans = 0;
        for(int i=0;i<nums.size();i++){
            for(auto &x:mpprev){
                mp[(x.first&nums[i])] += x.second;
            }
            mp[nums[i]]++;
            if(mp.find(k) != mp.end()) ans += mp[k];

            // cout << ans << " " << mp[9] << endl;
            mpprev = mp;
            mp.clear();
        }
        return ans;
    }
};
