            tree[id] = x[le];
            return;
        }
        int mid = (le+ri)>>1;
        build(lc,le,mid), build(rc,mid+1,ri);
        tree[id] = tree[lc] & tree[rc];
    }
    int que(int id,int le,int ri){
        if(le>=a && ri<=b)return tree[id];
        int mid = (le+ri)>>1;
        if(a<=mid && mid+1<=b)return que(lc,le,mid) & que(rc,mid+1,ri);
        if(a<=mid)return que(lc,le,mid);
        return que(rc,mid+1,ri);
    }
    int que(int l,int r){
        a = l, b = r;
        return que(1,1,n);
    }
}tree;

class Solution {
public:
    long long countSubarrays(vector<int>& nn, int k) {
        n = 0;
        for(int i:nn)x[++n] = i;
        tree.n = n;
        tree.build(1,1,n);
        int le,ri,mid,a,b,res;
        ll ans = 0;
        bool st;
        // cout<<tree.que(2,3)<<endl;
        rep(i,1,n){
            st = 0;
            le = i, ri = n;
            while(le<=ri){
                mid = (le+ri)>>1;
                res = tree.que(i,mid);
                st|= res==k;
                if(res>=k)a = mid, le = mid+1;
                else ri = mid-1;
            }
            le = i, ri = n;
            while(le<=ri){
                mid = (le+ri)>>1;
                res = tree.que(i,mid);
                st|= res==k;
                if(res<=k)b = mid, ri = mid-1;
                else le = mid+1;
            }
            if(st){
                // cout<<a<<" "<<b<<endl;
                ans+= a-b+1;
            }
        }
        return ans;
    }
};
