class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        vector<vector<int>> zero(32);

        for(int i=0 ; i<n ; i++){
            int x = v[i];
            for(int j =0 ; j<32 ; j++){
                if((x&1) == 0){
                    zero[j].push_back(i);
                }
                x/=2;
            }
        }
        // cout<<zero[0].size()<<" *** \n";
        // for(auto it : zero){
        //     for(auto i : it){
        //         cout<<i<<" ";
        //     }
        //     cout<<endl;
        // }
        long long ans = 0;
        vector<int> a;
        for(int i=0 ; i<32 ; i++){
            if((k&1) == 1){
                a.push_back(1);
            }else{
                a.push_back(0);
            }
            k/=2;
            // cout<<a[i]<<" ";
        }

        for(int i=0 ; i<n ; i++){
            int mi = i , mx = n;
            for(int j =0 ; j<32 ; j++){
                if(a[j] == 0){
                    int x = lower_bound(zero[j].begin() , zero[j].end() , i) - zero[j].begin();
                    if(x < zero[j].size()){
                        // cout<<i<<" "<<j<<" "<<zero[j][x]<<endl;
                        mi = max(mi , zero[j][x]);}
                    else{
                        // cout<<i<<" "<<j<<" "<<endl;
                        mi = n;
                    }
                }else if(a[j] == 1){
                    int x = lower_bound(zero[j].begin() , zero[j].end() , i) - zero[j].begin();
                    if(x < zero[j].size())
                        mx = min(mx , zero[j][x]);
                }
            }
            // cout<<mi<<" "<<mx<<endl;
            if(mi < mx){
                long long z = mx - mi;
                ans += z;
            }
        }
        return ans;
    }
};
