class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int sz = array.size();
        long long tl = 0;
        unordered_map<long long, long long> prResult;
        for (int ix = 0; ix < sz; ++ix) {
            unordered_map<long long, long long> currResult; 
            if (array[ix] == target) {
                ++tl;
            }
            currResult[array[ix]] = 1;
            for (auto& [value, hz] : prResult) {
                long long newResult = value & array[ix];
                if (newResult == target) {
                    tl += hz;
                }
                currResult[newResult] += hz;
            }

            prResult = currResult;
        }
        return tl;
    }
};
