class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        n = len(nums)
        note = Counter()

        for i in range(n):
            new_note = Counter()
            new_note[nums[i]] = 1
            for key in note:
                new_note[key & nums[i]] += note[key]
            for key in new_note:
                if key & nums[i] == k:
                    ans += new_note[key & nums[i]]
            note = new_note

        return ans
