class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
 int n = nums.size();
    long long count = 0;
    unordered_map<int, int> dp;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> new_dp;

        if (nums[i] == k) count++;
        new_dp[nums[i]] = 1;

        for (auto& [val, freq] : dp) {
            int new_and = val & nums[i];
            if (new_and == k) count += freq;
            new_dp[new_and] += freq;
        }

        dp = new_dp;
    }

    return count;
    }
};
