class Solution {
public:
    int sum(int i, int j, vector<vector<int>> &count){
        int ans = 0;
        for(int k=0; k< 32; k++){
            if( i == 0){ if( count[j][k] == j + 1)
                ans += 1<<k;
            }
            else if(count[j][k] - count[i-1][k] == j - i + 1){
                ans += 1<<k;
            }
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int j = 0, n = nums.size();
        vector<vector<int>> count(n, vector<int>(32, 0));
        for(int i=0; i< n; i++){
            for(int j=0; j<32; j++){
                if (!(nums[i] & (1<<j)) == 0)
                // cout<< ((nums[i] & (1<<j)) == 0);
                    count[i][j]++;
                if(i > 0)
                    count[i][j] += count[i-1][j];
                // cout<<count[i][j]<<" ";
            }
            // cout<<endl;
        }
        vector<long long> se;
        for(int i =0; i<n;i++){
            if((nums[i] & k) != k){
                se.push_back(i);
            }
        }
        se.push_back(n);
        for(int i =0 ; i<n && j<n; i++){
            if((nums[i] & k) == k){
                // continue;
                if( i > j)
                    j = i;
                // cout <<i<<" "<<j <<" "<<sum(i, j, count)<<endl;
                while( j<n && sum(i, j, count) > k )
                    j++;
                if(j < n && sum(i, j, count) == k){
                    // cout << i << j<<endl;
                    ans += se[lower_bound(se.begin(), se.end(), j)- se.begin()] - j;
                }
            }
        }
        return ans;
    }
};
