class Solution:
    def countSubarrays(self, a: List[int], k: int) -> int:
        n = len(a)
        r = 0
        d0 = defaultdict(int)
        for i in range(n):
            d1 = defaultdict(int)
            if (a[i] & k) == k:
                d1[a[i]] += 1
                for x in d0:
                    if ((y := x & a[i]) & k) == k:
                        d1[y] += d0[x]
                r += d1[k]
            d0 = d1
            #print(a[i], {x: d0[x] for x in d0})
        return r
