class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res,c=0, Counter()
        for v in nums:
            if v&k != k:
                c=Counter()
                continue
            c2=Counter()
            c2[v]+=1
            for v2 in c:
                c2[v&v2] += c[v2]
            c=c2
            res+=c[k]
        return res
