class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      int n = si(nums);
      vector<pair<int, int>> f;
      ll ans = 0;
      for (int i = 0; i < n; i++) {
        vector<pair<int, int>> g;
        f.push_back({nums[i], i});
        for (auto [x, j] : f) {
          int y = x & nums[i];
          if (g.empty() || g.back().first != y) {
            g.push_back({y, j});
          }
        }
        swap(f, g);
        for (int j = 0; j < si(f); j++) {
          if (f[j].first == k) {
            int to = (j == si(f)-1) ? i : f[j+1].second - 1;
            ans += to - f[j].second + 1;
            break;
          }
        }
      }
      return ans;
    }
};
