class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll, ll> m;
        ll ans = 0;

        for (auto i : nums) {
            map<ll, ll> nm;

            for (auto [e, f] : m) {
                nm[e & i] += f;
            }
            nm[i]++;

            ans += nm[k];

            m = nm;
        }

        return ans;
    }
};
