class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> f(n);
        vector<vector<int>> g(n,vector<int>(35));
        for(int i=0;i<n;i++){
            for(int j=0;j<31;j++){
                if(nums[i]>>j&1)g[i][j]=i==0?-1:g[i-1][j];
                else g[i][j]=i;
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            if((nums[i]&k)==k)f[i]=(i==0?1:f[i-1]+1);
            else f[i]=0;
            int mx=0;
            for(int j=0;j<31;j++){
                if(k>>j&1)continue;
                mx=max(mx,i-g[i][j]+1);
            }
            // cout<<f[i]<<" "<<mx<<"\n";
            ans+=max(0,f[i]-mx+1);
        }
        return ans;
    }
};
