class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<map<long long, long long>> bits(n);
        long long ans=0;
        bits[0][nums[0]]++;
        if(nums[0]==k) ans++;

        for(int i=1; i<n; i++){
            auto mp=bits[i-1];
            bits[i][nums[i]]++;
            for(auto p: mp){
                long long value=p.first; long long count=p.second;
                bits[i][nums[i]&value] += count;
            }
            for(auto p: bits[i]){
                if(p.first==k) ans=ans+p.second;
            }
        }
        return ans;
    }
};
