class Solution {
public:

    int res(vector<int>& cnt){
        int s=0;
        for(int i=0;i<30;i++){
            if(cnt[i]==0) s|=(1<<i);

        }
        return s;
    }


    long long f2(vector<int>& nums, int k){
        int n=nums.size();
        int l=0, r;
        long long ans=0;
        vector<int> cnt(30,0);
        for(int r=0;r<n;r++){
            for(int i=0;i<30;i++){
                if(nums[r]&(1<<i)) continue;
                else cnt[i]++;
            }
            while(l<=r && res(cnt)<k){
                for(int i=0;i<30;i++){
                    if(nums[l]&(1<<i)) continue;
                    else cnt[i]--;
                }
                l++;
            }
            if(res(cnt)>=k){
                ans+=(r-l+1);
            }
        }
        return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        long long a1=f2(nums, k);
        long long a2=f2(nums, k+1);
        return a1-a2;
    }
};
