class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        unordered_map<long long, long long> previousResults;
        long long resultCount = 0;
        int size = arr.size();




        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults;
            currentResults[arr[index]] = 1;

            resultCount += (arr[index] == target) ? 1 : 0;


            for (auto& [prevValue, frequency] : previousResults) {
                long long andResult = prevValue & arr[index];
                resultCount += (andResult == target) ? frequency : 0;
                currentResults[andResult] += frequency;
            }

            previousResults = currentResults;
        }

        return resultCount;
    }
};
