# define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll res=0;
        int n=nums.size();

        map<ll,ll>m;
        for(int i=0;i<n;i++)
        {
            map<ll,ll>temp;
            if(k==nums[i])
                res++;
            temp[nums[i]]=1;

            for(auto j:m)
            {
                ll a1=j.first;
                ll a2=j.second;
                ll x= a1 & nums[i];
                if(x==k)
                    res+=a2;

                temp[x]+=a2;
            }
            m=temp;
        }
        return res;
    }
};
