class Solution {
public:
    long long countSubarrays(vector<int>& nums, int t) {
        long long ans = 0;
        unordered_map<int, long long> m;
        for (auto i : nums) {
            unordered_map<int, long long> m2;
            m2[i] = 1;
            for (auto &[k, v] : m) m2[k & i] += v;
            m.clear();
            for (auto &[k, v] : m2) {
                if (k == t) ans += v;
                m[k] = v;
            }
        }
        return ans;
    }
};
