class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res=0
        h=defaultdict(int)
        for num in nums:
            new_h=defaultdict(int)
            for key in h:
                new_h[key&num]+=h[key]
            new_h[num]+=1
            h=new_h
            res+=h[k]
        return res
