class Solution {
public:
   long long countSubarrays(vector<int>& sankhya, int k) {
        int n = sankhya.size();
        long long ginti = 0;

        unordered_map<long long, long long> pichleNateeje;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> abhiNateeje;

            if (sankhya[i] == k) {
                ++ginti;
            }
            abhiNateeje[sankhya[i]] = 1;

            for (auto& [muly, matra] : pichleNateeje) {
                long long nayaAndParinaam = muly & sankhya[i];
                if (nayaAndParinaam == k) {
                    ginti += matra;
                }
                abhiNateeje[nayaAndParinaam] += matra;
            }

            pichleNateeje = abhiNateeje;
        }

        return ginti;
    }
};
