#define min_h priority_queue<int,vector<int>,greater<int>>
#define max_hll priority_queue<ll>
#define min_hll priority_queue<ll,vector<ll>,greater<ll>>
#define pb push_back
#define ff first
#define ss second
#define all(c) c.begin(),c.end()
#define allr(c) c.rbegin(),c.rend()
#define popcount __builtin_popcountll
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans(0);
        map<ll,ll> prev;
        fr(i,0,n)
        {
            map<ll,ll> crr;
            if(nums[i] == k) ++ans;
            crr[nums[i]] = 1;
            for(auto &x : prev)
            {
                ll res = (x.ff&nums[i]);
                if(res == k) ans += x.ss;
                crr[res] += x.ss;
            }
            prev = crr;
        }
        return ans;
    }
};
