class Solution:
    def countSubarrays(self, A: List[int], k: int) -> int:
        ans=0
        cnt0=[0]*31
        cnt1=[0]*31
        full=(1<<31)-1
        i=0
        j=0
        c0=full
        c1=full
        n=len(A)
        for z,a in enumerate(A):
            c0&=a
            c1&=a
            for d in range(31):
                if a>>d&1:
                    cnt0[d]+=1
                    cnt1[d]+=1
            while c0<k and i<=z:
                b=A[i]
                for d in range(31):
                    if b>>d&1:
                        cnt0[d]-=1
                    if cnt0[d]==z-i and c0>>d&1==0:
                        c0^=1<<d
                i+=1

            while c1<=k and j<n and j<=z:
                b=A[j]
                for d in range(31):
                    if b>>d&1:
                        cnt1[d]-=1
                    if cnt1[d]==z-j and c1>>d&1==0:
                        c1^=1<<d
                j+=1

            if c0==k:
                ans+=j-i

        return ans
