class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        ll ans = 0;
        map<ll,ll>m;
        for(int i = 0;i < n;i++){
            if(v[i] == k) ans++;
            map<ll,ll>temp;
            temp[v[i]]++;
            for(auto &x : m){
                int val = (x.first) & v[i];
                if(val == k) ans += x.second;
                temp[val] += x.second;
            }
            m = temp;
        }
        return ans;
    }
};
