#define ll long long
class Solution {
public:
ll fun(const vector<int>& v, int k) {
    ll n = v.size();
    ll count = 0;
    unordered_map<ll,ll> mp;

    for (int i = 0; i < n; ++i) {
        unordered_map<ll,ll> sndmp;
        sndmp[v[i]] = 1;

        for (auto& pair : mp) {
            ll newAnd = pair.first & v[i];
            sndmp[newAnd] += pair.second;
        }

        if (sndmp.find(k) != sndmp.end()) {
            count += sndmp[k];
        }

        mp = sndmp;
    }

    return count;
}
    long long countSubarrays(vector<int>& v, int k) {
      return 1ll * fun(v, k)  ;
    }
};
