#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n = nums.size();


        ll ans = 0;
        for(int i=0;i<n;i++){
            if(nums[i] == k) ans++;
        }
        unordered_map<ll,ll> mp;

        for(int i=0;i<n;i++){
            unordered_map<ll,ll> curr;

            curr[nums[i]] = 1;

            for(auto &it: mp){
                ll currAnd = nums[i]&it.first;
                if(currAnd == k) ans += it.second;

                curr[currAnd] += it.second;
            }

            mp = curr;
        }

        return ans;


    }
};
