class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long ans = 0;
        map<long long, int> dp;
        for(int i = 0; i < n; i++)
        {
            map<long long, int> ndp;
            ndp[a[i]] = 1;
            for(auto &[x, y] : dp)
            {
                ndp[x & a[i]] += y;
                if((x & a[i]) == k) ans += y;
            }
            if(a[i] == k) ans++;
            dp = ndp;
        }
        return ans;
    }
};
