class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ands = []
        mask = (1 << 32) - 1
        ans = 0
        for x in nums:
            ands.append([mask, 1])
            j = 0
            for i in range(len(ands)):
                ands[i][0] &= x
                # if ands[i][0] < k:
                #     break
                if ands[i][0] == ands[j][0]:
                    if i != j:
                        ands[j][1] += ands[i][1]
                else:
                    j += 1
                    ands[j] = ands[i]
            del ands[j+1:]
            # print(x, ands)
            for y, c in ands:
                if y == k:
                    ans += c
        return ans
