struct segTree{
    int CONST = -1;
    int op(int a,int b){ return (a & b); }

    int n; vector<int> tree;
    segTree(int n) : n(n), tree(2*n,CONST) {}
    segTree(vector<int> &a,int n) : segTree(n) { for(int i = 0;i < n;i++) update(i,a[i]); }

    void update(int p, int x){
        for(tree[p+=n]=x; p; p>>=1) tree[p>>1] = op(tree[p], tree[p^1]);
    }

    int query(int l, int r){
        int res = CONST;
        for(l+=n, r+=n+1; l<r; l>>=1, r>>=1){
            if(l&1) res = op(res, tree[l++]);
            if(r&1) res = op(res, tree[--r]);
        }
        return res;
    }
};

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        segTree st(nums,n);

        long long cnt = 0;
        for(int i = 0;i < n;i++){
            int s = i,e = n - 1,res1 = s - 1;
            while(s <= e){
                int mid = s + (e - s)/2;
                if(st.query(i,mid) > k){
                    res1 = mid;
                    s = mid + 1;
                }
                else e = mid - 1;
            }
            int res2 = s;
            s = i,e = n - 1;
            while(s <= e){
                int mid = s + (e - s)/2;
                if(st.query(i,mid) >= k){
                    res2 = mid;
                    s = mid + 1;
                }
                else e = mid - 1;
            }
            if(st.query(i,res2) == k){
                cnt += (res2 - res1);
            }
        }
        return cnt;
    }
};
