class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ans = 0
        cnter = defaultdict(int)
        for x in nums:
            cnter_new = defaultdict(int)
            cnter[x] += 1
            for and_, cnt in cnter.items():
                cnter_new[x & and_] += cnt
                if x & and_ == k:
                    ans += cnt
            cnter = cnter_new
        return ans
