class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int dp[35];
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        long long res=0;
        for(int i=0;i<n;i++){
            int l=-1,r=i;
            for(int j=0,a=1;j<32;j++,a<<=1){
                if((nums[i]&a)==0)dp[j]=i;

                if(k&a)l=max(l,dp[j]);
                else r=min(r,dp[j]);
            }
            if(r>l)res+=r-l;
        }
        return res;
    }
};
