class Solution {
public:
    int create_and(vector<vector<int>> &pand,int in1,int in2)
    {
        int mul=1;
        int ans=0;
        for(int i=0;i<31;i++)
        {
            int no=0;
            if(in2>0)
            no=pand[in2-1][i];
            else
            no=0;
            if(pand[in1][i]-no==in1-in2+1)
            ans+=mul;
            mul=mul<<1;
        }
        //cout<<in1<<" "<<in2<<" "<<ans<<endl;
        return ans;
    }
    long long int atleast(vector<int> &nums,vector<vector<int>> &pand,int k)
    {
        int s=0;
        int n=nums.size();
        long long int ans=0;
        for(int i=0;i<n;i++)
        {
            while(s<=i && create_and(pand,i,s)<k)
            {
                s++;
            }
            ans+=i-s+1;
            //cout<<i<<" "<<s<<endl; 

        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> pand(n,vector<int>(31,0));
        for(int i=0;i<n;i++)
        {
            int no=nums[i];
            int in=0;
            while(no>0)
            {
                if(no%2==1)
                pand[i][in]++;
                no=no/2;
                in++;
            }
            for(int j=0;j<31;j++)
            {
                if(no%2==1)
                pand[i][j]++;
                if(i>0)
                pand[i][j]+=pand[i-1][j];
                no=no/2;
            //cout<<pand[i][j]<<" ";
            }
            //cout<<endl;
        }
        return atleast(nums,pand,k)- atleast(nums,pand,k+1);
    }
};
