class Solution {
public:
    long long countSubarrays(vector<int>& number, int w) {
        int n = number.size();
        long long count = 0;

        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (number[i] == w) {
                ++count;
            }
            curr[number[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newAndResult = val & number[i];
                if (newAndResult == w) {
                    count += freq;
                }
                curr[newAndResult] += freq;
            }

            prev = curr;
        }

        return count;
    }
};
