class Solution {
public:
typedef long long ll;
    long long countSubarrays(vector<int>&nums, int k) {
     ll n = nums.size();
    long long  count = 0;
    std::unordered_map<ll, ll> and_count;

    for (int i = 0; i < n; ++i) {
        std::unordered_map<ll, ll> new_and_count;
        new_and_count[nums[i]] = 1;
        for (auto &[key, value] : and_count) {
            ll new_and = key & nums[i];
            new_and_count[new_and] += value;
        }
        and_count = new_and_count;
        count += and_count[k];
    }

    return count;


    }
};
