class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> prev, cur;

        long long ans = 0;
        long long n = nums.size();
        for(int j=0; j<n; j++){
            cur.clear();
            cur[nums[j]]++;
            for(auto& i: prev) {
                long long val = (i.first & nums[j]);
                if(cur.find(val)==cur.end()) cur[val] = 0;
                cur[val] += i.second;
            }
            ans += cur[k];
            prev = cur;
        }
        return ans;
    }
};
