class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
            int n = nums.size();
        unordered_map<int, int> countMap;
        long long result = 0;

        for (int num : nums) {
            unordered_map<int, int> newCountMap;

            if (num == k) {
                result++;
            }

            for (auto& [key, count] : countMap) {
                int newAND = key & num;
                if (newAND == k) {
                    result += count;
                }
                newCountMap[newAND] += count;
            }

            newCountMap[num]++;
            countMap = newCountMap;
        }

        return result;

    }
};
