class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        dp = Counter()
        ans = 0
        for n in nums:
            next_dp = Counter()
            dp[n] += 1
            for m, v in dp.items():
                if n & m == k:
                    ans += v
                next_dp[n & m] += v
            dp = next_dp

        return ans
