class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> prevAnds;
        long long ans = 0;
        for(int i = 0; i < n; i++) {
            map<int, int> curAnds;
            if(nums[i] >= k)
                curAnds[nums[i]] = 1;
            for(auto& [val, freq] : prevAnds) {
                int newV = (val & nums[i]);
                if(newV >= k)
                    curAnds[newV] += freq;
            }
            ans += curAnds[k];
            prevAnds = curAnds;
        }
        return ans;
    }
};
