class Solution:
    def countSubarrays(self, nums: List[int], target: int) -> int:
        n = len(nums)
        cnt = 0
        prev = defaultdict(int)
        for num in nums:
            curr = defaultdict(int)
            if num == target:
                cnt += 1
            curr[num] = 1
            for i, count in prev.items():
                j = i & num
                if j == target:
                    cnt += count
                curr[j] += count
            prev = curr
        return cnt
