#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll ans=0,n=a.size();
        vector<int> dp(32,-1);
        for(int i=0; i<n; i++){
            for(int j=0; j<32; j++){
                if(!((a[i]>>j)&1)) dp[j]=i;
            }

            int zero=i,one=-1;
            for(int j=0; j<32; j++){
                if((k>>j)&1) one=max(one,dp[j]);
                else zero=min(zero,dp[j]);
            }
            // for(int j=0; j<32; j++) cout<<dp[j]<<" ";
            // cout<<endl;
            // cout<<one<<" "<<zero<<endl;
            if(zero>=one){
                // cout<<i<<" "<<i-one<<endl;
                ans=(ans+zero-one);
            }
        }
        return ans;
    }
};
