#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {

        vector<int>lastzero(31,-1);
        ll ans = 0;
        int last = -1;
        for(int i =0 ;i<nums.size();i++)
        {
            int andres=0;
            int minzero=i;
            for(int j = 0; j<31 ; j++)
            {
                if(!((nums[i]>>j)&1))
                {
                    lastzero[j]=i;
                    if((k>>j)&1)
                    {
                        last = i;
                        break;
                    } 
                }
                if(lastzero[j]<=last)
                {
                    andres+= (1<<j);
                }

                if(((k>>j)&1)==0)
                {
                    minzero=min(minzero,lastzero[j]);
                }
                // cout<<i <<" "<<j<<" "<<andres<<" "<<lastzero[j]<<" "<<minzero<<" "<<ans<<endl;
            }

            if(andres == k)
                ans= ans + 1ll*max(minzero-last,0);
        }    
        return ans;
    }
};
