class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long res = 0;
        vector<int> b(a.size());
        int v;
        for (int i = 0; i < a.size(); ++i) {
            if ((a[i] & k) != k) {
                continue;
            }
            int j = i;
            while (j < a.size() && (a[j] & k) == k) {
                ++j;
            }
            res += (j - i) * (j - i + 1ll) / 2;

            for (int l = i, r = i, md = i - 1; l < j; ++l) {
                if (a[l] == k) {
                    continue;
                }
                if (md < l) {
                    v = a[l];
                    r = l;
                    while (r < j && (v & a[r]) > k) {
                        v &= a[r++];
                    }
                    md = r - 1;
                    b[md] = a[md];
                    for (int t = md - 1; t >= l; --t) {
                        b[t] = a[t] & b[t + 1];
                    }
                    v = -1;
                }
                while (r < j && (b[l] & v & a[r]) > k) {
                    v &= a[r++];
                }
                res -= r - l;
            }

            i = j;
        }
        return res;
    }
};
