class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        map<int, int> and_map; 

        for (int i = 0; i < n; ++i) {
            map<int, int> new_and_map;

            for (auto &[and_val, freq] : and_map) {
                int new_and_val = and_val & nums[i];
                new_and_map[new_and_val] += freq;
                if (new_and_val == k) {
                    count += freq;
                }
            }

            new_and_map[nums[i]]++;
            if (nums[i] == k) {
                count++;
            }

            and_map = new_and_map;
        }

        return count;
    }
};
