from collections import defaultdict
from typing import List


class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0

        prev = defaultdict(int)

        for i in range(n):
            curr = defaultdict(int)

            if nums[i] == k:
                count += 1
            curr[nums[i]] = 1

            for val, freq in prev.items():
                new_and_result = val & nums[i]
                if new_and_result == k:
                    count += freq
                curr[new_and_result] += freq

            prev = curr

        return count
