class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        int size =nums.size();
        long long res = 0;
        for(int i=0;i<size;i++)
        {
            unordered_map<int,int> new_mp;
            new_mp[nums[i]]++;
            for(auto &[key,value]:mp)
            {
                int target = nums[i]&key;
                new_mp[target]+=mp[key];
            }
            res+=new_mp[k];
            cout<<res<<" ";
            mp=new_mp;
        }
        return res;
    }
};
