class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        vector<vector<int>> next0(n + 1, vector<int>(31, n));
        for (int i = n - 1; i >= 0; i--) {
            next0[i] = next0[i + 1];
            for (int j = 0; j < 31; j++) {
                if (nums[i] & (1 << j)) {
                    //
                } else {
                    next0[i][j] = i;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            int left = -1, right = n - 1;
            for (int j = 0; j < 31; j++) {
                if (k & (1 << j)) {
                    right = min(right, next0[i][j] - 1);
                } else {
                    left = max(left, next0[i][j]);
                }
            }
            if (left == n)
                continue;
            ans += max(0, right - left + 1);
        }
        return ans;
    }
};
