#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll, ll> mp;

        ll ans = 0;
        for(ll i = 0; i < nums.size(); ++i){
            map<ll, ll> mp2;

            for(auto &[a, b] : mp){
                ll temp = a & nums[i];

                mp2[temp] += b;

                if(temp == k){
                    ans += b;
                }
            }

            mp2[nums[i]]++;
            if(nums[i] == k) ++ans;

            mp = mp2;
        }

        return ans;
    }
};
