class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        cc = collections.Counter()
        sol = 0
        for x in nums:
            cc2 = collections.Counter()
            for y, c in cc.items():
                cc2[y & x] += c
            cc2[x] += 1
            cc = cc2
            sol += cc[k]
        return sol
