class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n=v.size();
        map<long long int,long long int> mp;
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            map<long long,long long> temp;
            for(auto itr=mp.begin();itr!=mp.end();itr++)
            {
                temp[(itr->first)&v[i]]+=itr->second;
            }
             temp[v[i]]++;
             mp=temp;
             //cout<<mp[k]<<endl;
             ans+=mp[k];
        }
        return ans;
    }
};
