class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        std::unordered_map<long long, long long> mp1;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<long long, long long> mp2; 
            if (nums[i] == k) ans++;
            mp2[nums[i]] = 1;
            for (auto& [val, freq] : mp1) {
                long long next = val & nums[i];
                if (next == k) ans += freq;
                mp2[next] += freq;
            }
            mp1 = mp2;
        }
        return ans;
    }
};
